import React, { Component } from 'react';
import {Link} from 'react-router-dom';


class RechercheRecette extends Component {

    state = {
        recipes: [

        ]
    };

    componentDidMount() {
       this.getRecipes();
    }

    getRecipes = _ => {
        fetch(`http://localhost:4000/recipes`)
        .then(response => response.json())
        .then(response => this.setState({ recipes: response.data}))
        .catch(err => console.err(err))
    }

    renderRecipes = ({id_recette, libelle_recette}) => <div key={id_recette}>{libelle_recette}</div>

    renderRecipes = ({id_recette, libelle_recette, instructions_recette, difficulte, temps_prep}) => (
        <div key={id_recette} class="card" style="width: 18rem;">
            <img src="..." class="card-img-top" alt="..." />
            <div class="card-body">
                <h5 class="card-title">Card title</h5>
                <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                <a href="#" class="btn btn-primary">Go somewhere</a>
            </div>
        </div>
    )


    render(){
        const {recipes} = this.state;
        return (

            <div>
                {recipes.map(this.renderRecipes)}
            </div>
        )
    }



}

export default RechercheRecette;
