import React, { Component } from 'react';
import {Link} from 'react-router-dom';


class RechercheRecette extends Component {

    state = {
        recipes: [

        ]
    };

    componentDidMount() {
       this.getRecipes();
    }

    getRecipes = _ => {
        fetch(`http://localhost:4000/recipes`)
        .then(response => response.json())
        .then(response => this.setState({ recipes: response.data}))
        .catch(err => console.err(err))
    }

    renderRecipes = ({id_recette, libelle_recette}) => (
        <div>
            <div key={id_recette}>{libelle_recette}</div>
        </div>
    )

    renderRecipes2 = ({id_recette, libelle_recette, instructions_recette, difficulte, temps_prep}) => (
        <div class="card" style={{width: '18rem'}}>
            <img src="..." className="card-img-top" alt="..." />
            <div class="card-body">
                <h5 className="card-title">Card title</h5>
                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                <Link to="/" className="btn btn-primary">Go somewhere</Link>
            </div>
        </div>
    )



    render(){
        const {recipes} = this.state;
        return (

            <div>
                {recipes.map(this.renderRecipes2)}
            </div>
        )
    }



}

export default RechercheRecette;
